import React, { useState } from 'react';
import './Shop.css';

const products = [
  { id: 1, category: 'Toys', name: 'Toy Car', price: 10, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRa7_0dQ7hu9Wp1Eod8v45qFa5kIe3fGK7xEw&usqp=CAU' },
  { id: 2, category: 'Toys', name: 'Action Figure', price: 15, image: 'data:image/jpeg;base64,' },
  { id: 3, category: 'Books', name: 'Diary of a Wimpy Kid Rodrick Rules', price: 20, image: 'data:image/jpeg;base64,' },
  { id: 4, category: 'Books', name: 'Wonder', price: 25, image: 'data:image/jpeg;base64,' },
];

function Shop() {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (product) => {
    setCart(cart.filter((item, index) => index !== product));
  };

  return (
    <div className="shop-container">
      <h1 className="shop-header">Shop</h1>
      <div className="shop-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h2>{product.name}</h2>
            <p>${product.price}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <div className="cart-container">
        <h2>Cart</h2>
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          cart.map((item, index) => (
            <div key={index} className="cart-item">
              <p>{item.name} - ${item.price}</p>
              <button onClick={() => removeFromCart(index)}>Remove</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Shop;
